int cube[5][5][5]; 
int tableau_solution[25][5][3];


void remplir_cube(int _piece[5][3])
{
    for (int iBlock=0; iBlock<=4; iBlock++)
    {
        int x, y, z;
        x = _piece[iBlock][0];
        y = _piece[iBlock][1];
        z = _piece[iBlock][2];
        cube[x][y][z] = 1;
    }
}

void vider_cube(int _piece[5][3])
{
    int x, y, z;
    for (int iBlock=0; iBlock<=4; iBlock++)
    {       
        x = _piece[iBlock][0];
        y = _piece[iBlock][1];
        z = _piece[iBlock][2];
        cube[x][y][z] = 0;
    }
}

bool piece_possible(int _piece[5][3])
{
    for (int iBlock = 0; iBlock <= 4; iBlock++)
    {
        int x, y, z;
        x = _piece[iBlock][0];
        y = _piece[iBlock][1];
        z = _piece[iBlock][2];
        if (cube[x][y][z] == 1)
        {
            return false;
        }
        
    }
    return true;
}
/*
Se débrouiller pour ne pas avoir une complexité de 320^25... Stocker les positions...Faire qqch
 */
int solution(int nb_pieces)
{
    if (nb_pieces == 21)
    {
        for (int iCase = 0; iCase <= 24; iCase++) 
        {   
            printf("%d   " ,iCase+1);
            for (int iBlock = 0; iBlock <= 4; iBlock++) 
            {
                for (int iCoord = 0; iCoord <= 2; iCoord++) 
                {
                    printf("%d ", tableau_solution[iCase][iBlock][iCoord]) ;
                }
                printf("  ");
            }
            printf(" \n");
        }
        exit(0);      
    } 
    else
    {
        int _piece_[5][3];
        for (int iPiece = 0; iPiece < 320; iPiece++)
        {
            for (int iBlock = 0; iBlock <= 4; iBlock++)
            {
                for (int iCoords = 0; iCoords <= 2; iCoords++)
                {
                    _piece_[iBlock][iCoords] = possibility[iPiece][iBlock][iCoords];
                }
            }

            if (piece_possible(_piece_))
            {
                remplir_cube(_piece_);
                for (int iBlock = 0; iBlock <= 4; iBlock++)
                {
                    for (int iCoords = 0; iCoords <= 2; iCoords++)
                    {
                        tableau_solution[nb_pieces][iBlock][iCoords] = _piece_[iBlock][iCoords];
                    }
                }
                solution(nb_pieces+1);
                vider_cube(_piece_);
            }
        }
    }
    return 0;
}



int main()
{
    for (int x = 0; x <= 4; x++) 
        for (int y = 0; y <= 4; y++) 
            for (int z = 0; z <= 4; z++) 
                cube[x][y][z] = 0;
    
    for (int x = 0; x <= 24; x++) 
        for (int y = 0; y <= 4; y++) 
            for (int z = 0; z <= 2; z++) 
            {
                tableau_solution[x][y][z] = 0;
            }
    solution(0);
        
}

